                            {
                                steps = 0;
                                curPos = seed[pos2];
                            }
                        }
                        break;
                    case 6: //jump if false
                        {
                            steps = 3;
                            var pos1 = instructions.ElementAtOrDefault(1) == 1 ? curPos + 1 : seed[curPos + 1];
                            var pos2 = instructions.ElementAtOrDefault(2) == 1 ? curPos + 2 : seed[curPos + 2];

                            if (seed[pos1] == 0)
                            {
                                steps = 0;
                                curPos = seed[pos2];
                            }
                        }
                        break;
                    case 7: //less than
                        {
                            steps = 4;
                            var pos1 = instructions.ElementAtOrDefault(1) == 1 ? curPos + 1 : seed[curPos + 1];
                            var pos2 = instructions.ElementAtOrDefault(2) == 1 ? curPos + 2 : seed[curPos + 2];
                            var pos3 = instructions.ElementAtOrDefault(3) == 1 ? curPos + 3 : seed[curPos + 3];

                            seed[pos3] = seed[pos1] < seed[pos2] ? 1 : 0;
                        }
                        break;
                    case 8: //equals
                        {
                            steps = 4;
                            var pos1 = instructions.ElementAtOrDefault(1) == 1 ? curPos + 1 : seed[curPos + 1];
                            var pos2 = instructions.ElementAtOrDefault(2) == 1 ? curPos + 2 : seed[curPos + 2];
                            var pos3 = instructions.ElementAtOrDefault(3) == 1 ? curPos + 3 : seed[curPos + 3];

                            seed[pos3] = seed[pos1] == seed[pos2] ? 1 : 0;
                        }
                        break;
                    case 99: //exit
                        return returner;
                    default: //we fucked up
                        return null;
                }
                #endregion

                curPos += steps;
            }

            return returner;
        }
    }


    public class Moon
    {
        public int PosX;
        public int PosY;
        public int PosZ;

        public int VelX;
        public int VelY;
        public int VelZ;

        public int AccelX;
        public int AccelY;
        public int AccelZ;

        public Moon(int x, int y, int z)
        {
            PosX = x;
            PosY = y;
            PosZ = z;

            VelX = 0;
            VelY = 0;
            VelZ = 0;

        }


        public void Move(int x, int y, int z)
        {
            VelX += x;
            VelY += y;
            VelZ += z;

            PosX += VelX;
            PosY += VelY;
            PosZ += VelZ;

            AccelX = 0;
            AccelY = 0;
            AccelZ = 0;
        }

        public void PrepMove(int x, int y, int z)
        {
            AccelX = x;
            AccelY = y;
            AccelZ = z;
        }

        public void Move()
        {
            VelX += AccelX;
            VelY += AccelY;
            VelZ += AccelZ;

            PosX += VelX;
            PosY += VelY;
            PosZ += VelZ;

            AccelX = 0;
            AccelY = 0;
            AccelZ = 0;
        }

        public int Energy()
        {
            int potential = Math.Abs(PosX) + Math.Abs(PosY) + Math.Abs(PosZ);
            int kinetic = Math.Abs(VelX) + Math.Abs(VelY) + Math.Abs(VelZ);

            return potential * kinetic;
        }


        public string GetPos()
        {
            return $"<x={PosX}, y={PosY}, z={PosZ}>";
        }

        public string GetVel()
        {
            return $"<x={VelX}, y={VelY}, z={VelZ}>";
        }
    }
}
